mapper.select:
	UPDATE ADDRESS
	   SET ADDRESS_ZIPCODE	=#{addressZipcode}
	      ,ADDRESS			=#{Address}
	      ,ADDRESS_DETAILS	=#{addressDetails}
	      ,MAIN_ADDRESS 	=#{mainAddress}
 	 WHERE USER_ID			=#{userId}

mapper.update:
UPDATE ADDRESS 
	SET ADDRESS_ZIPCODE 	=#{addressZipcode}
	   ,ADDRESS 			=#{address}
       ,ADDRESS_DETAILS 	=#{addressDetails}
 	   ,MAIN_ADDRESS 		=#{mainAddress}
 	   ,ADDRESS_GET_NM		=#{addressGetNm}
       ,ADDRESS_GET_PHONE	=#{addressGetPhone}
 	WHERE USER_ID			=#{userId}
