mapper.insert:
		INSERT INTO PRODUCT_LIST(
				    PROD_L_NUM
			       ,PROD_L_CODE
			       ,PROD_L_OPTION1
			       ,PROD_L_OPTION2
			       ,PROD_L_OPTION3
			       ,PROD_L_OPTION4
			       ,PROD_L_OPTION5
			       ,PROD_L_PRODNM
			       ,PROD_L_PRICE
			       ,PROD_L_UPLOADDATE
			       ,PROD_L_COMPNM
			       ,PROD_L_MAINIMG
			       ,PROD_L_SUBIMG
			       ,PROD_L_DETAIL
			       ,PROD_L_QUANTITY
           		    )
			 VALUES (
					(SELECT PROD_L_NUM
		       		 FROM   (SELECT IFNULL(MAX(CAST(PROD_L_NUM AS UNSIGNED)), 0) + 1 AS PROD_L_NUM
				 	   	       FROM PRODUCT_LIST) A)
			       ,#{prodLCode}
			       ,#{prodLOption1}
			       ,#{prodLOption2}
			       ,#{prodLOption3}
			       ,#{prodLOption4}
			       ,#{prodLOption5}
			       ,#{prodLProdnm}
			       ,#{prodLPrice}
			       ,#{prodLUploaddate}
			       ,#{prodLCompnm}
			       ,#{prodLMainimg}
			       ,#{prodLSubimg}
			       ,#{prodLDetail}
			       ,#{prodLQuantity}
			  		)

mapper.select:
SELECT PROD_L_NUM ,PROD_L_CODE ,PROD_L_OPTION1 ,PROD_L_OPTION2 ,PROD_L_PRODNM ,PROD_L_PRICE ,PROD_L_QUANTITY FROM PRODUCT_LIST WHERE PROD_L_COMPNM=#{compNm}
AND PROD_L_QUANTITY >= 50
AND PROD_L_QUANTITY < 50 AND PROD_L_QUANTITY > 0
AND PROD_L_QUANTITY = 0
AND ${columnNm} LIKE CONCAT('%', #{searchKeyWord}, '%')
ORDER BY PROD_L_NUM DESC LIMIT #{startRow} ,#{pageSize}

mapper.update:
	UPDATE  PRODUCT_LIST
	   SET  PROD_L_PRICE    =#{prodLPrice}
		   ,PROD_L_MAINIMG  =#{prodLMainimg}
		   ,PROD_L_SUBIMG   =#{prodLSubimg}
		   ,PROD_L_DETAIL   =#{prodLDetail}
		   ,PROD_L_QUANTITY =#{prodLQuantity}
     WHERE  PROD_L_CODE     =#{prodLCode}

mapper.select:
SELECT *
  FROM COMPANY_INFO
 WHERE COMP_ID=#{compId}
 
   AND  COMP_PASS = ${compPass}

mapper.update:
UPDATE    COMPANY_INFO
   SET	  COMP_ZIPCODE		=#{compZipcode}
	   	 ,COMP_ADDRESS		=#{compAddress}
	   	 ,COMP_DETADDRESS	=#{compDetaddress}
	   	 ,COMP_EXTADDRESS	=#{compExtaddress}
		 ,COMP_PHONE		=#{compPhone}
 WHERE    COMP_ID			=#{compId}

UPDATE COMPANY_INFO
   SET COMP_PASS	=#{compPass}
 WHERE COMP_ID		=#{compId}

mapper.delete:
DELETE FROM COMPANY_INFO
 WHERE 		COMP_ID		=#{compId}
   AND 		COMP_PASS	=#{compPass}
