mapper.insert:
		INSERT INTO PRODUCT_LIST(
				    PROD_L_NUM
			       ,PROD_L_CODE
			       ,PROD_L_OPTION1
			       ,PROD_L_OPTION2
			       ,PROD_L_OPTION3
			       ,PROD_L_OPTION4
			       ,PROD_L_OPTION5
			       ,PROD_L_PRODNM
			       ,PROD_L_PRICE
			       ,PROD_L_UPDATE
			       ,PROD_L_COMPNM
			       ,PROD_L_MAINIMG
			       ,PROD_L_SUBIMG
			       ,PROD_L_DETAIL
			       ,PROD_L_QUANTITY
           		    )
			 VALUES (
					(SELECT PROD_L_NUM
		       		 FROM   (SELECT IFNULL(MAX(CAST(PROD_L_NUM AS UNSIGNED)), 0) + 1 AS PROD_L_NUM
				 	   	       FROM PRODUCT_LIST) A)
			       ,#{prodLCode}
			       ,#{prodLOption1}
			       ,#{prodLOption2}
			       ,#{prodLOption3}
			       ,#{prodLOption4}
			       ,#{prodLOption5}
			       ,#{prodLProdnm}
			       ,#{prodLPrice}
			       ,#{prodLUpdate}
			       ,#{prodLCompnm}
			       ,#{prodLMainimg}
			       ,#{prodLSubimg}
			       ,#{prodLDetail}
			       ,#{prodLQuantity}
			  		)
