mapper.insert:
		INSERT INTO PRODUCT_LIST(
				    PROD_L_NUM
			       ,PROD_L_CODE
			       ,PROD_L_TYPE
			       ,PROD_L_OPTION1
			       ,PROD_L_OPTION2
			       ,PROD_L_PRODNM
			       ,PROD_L_PRICE
			       ,PROD_L_UPDATE
			       ,PROD_L_COMPNM
			       ,PROD_L_MAINIMG
			       ,PROD_L_SUBIMG
			       ,PROD_L_DETAIL
			       ,PROD_L_QUANTITY
           		    )
			 VALUES (

		       FROM (SELECT IFNULL(MAX(CAST(PROD_L_NUM AS UNSIGNED)), 0) + 1 AS PROD_L_NUM
				 	   FROM PROD_L_NUM) A)
			       ,#{prodLCode}
			       ,#{prodLType}
			       ,#{prodLOption1}
			       ,#{prodLOption2}
			       ,#{prodLProdnm}
			       ,#{prodLPrice}
			       ,#{prodLUpdate}
			       ,#{prodLCompnm}
			       ,#{prodLMainimg}
			       ,#{prodLSubimg}
			       ,#{prodLDetail}
			       ,#{prodLQuantity}
			  		)

insert.if:
        insert into PROD_OPTION(
        		   ,PROD_OPTION_NUM
                   ,PROD_OPTION_CODE
			       ,PROD_OPTION_NM
        )values
        
        (
        	(SELECT PROD_OPTION_NUM
		       FROM (SELECT IFNULL(MAX(CAST(PROD_OPTION_NUM AS UNSIGNED)), 0) + 1 AS PROD_OPTION_NUM
				 	   FROM PROD_OPTION) A)
            	  ,#{item.prodLOptionKey}
            	  ,#{item.prodOptionNmKey}
        )
