mapper.insert:
			INSERT INTO ORDER_LIST(
				 ORD_NUM
			 	,ORD_L_USER
			 	,ORD_L_CODE 
			 	,ORD_L_QUANTITY
			 	,ORD_L_PRICE
			 	,ORD_DELIVERY_STATUS
				,ORD_PURCHASESTATUS
				)
			VALUE(
				  (SELECT ORD_NUM
                    FROM   (SELECT IFNULL(MAX(CAST(ORD_NUM AS UNSIGNED)), 0) + 1 AS ORD_NUM
                             FROM ORDER_LIST) A)
				 ,#{ordLUser}
				 ,#{ordLCode}
				 ,#{ordLQuantity}
				 ,#{ordLPrice}
				 ,"0"
				 ,"Y"
			)

mapper.update:
UPDATE PRODUCT_LIST 
	SET PROD_L_QUANTITY = PROD_L_QUANTITY - #{ordLQuantity}
 	WHERE PROD_L_CODE		=#{prodLCode}

mapper.insert:
INSERT INTO POINT(
	POINT_NUM
   ,USER_ID
   ,POINT_DATE
   ,POINT_TYPE
   ,POINT_NOW
   ,POINT_USED
   ,POINT_CHARGE
	)
VALUES(
	 (SELECT A.POINTNUM
	    FROM (SELECT MAX(POINT_NUM) + 1 AS POINTNUM
	    	    FROM POINT) A)
	,#{userId}
	,#{pointDate}
	,"포인트 사용"
	,#{pointNow}
	,#{pointUsed}
	,0
	)

mapper.update:
UPDATE COUPON
	SET COU_YN = "N"
	WHERE COU_NUM =#{couNum}
