mapper.select:
		SELECT COUNT(*)
		  FROM POINT
   		 WHERE USER_ID= #{userId}

		SELECT * 
		  FROM POINT
 		 WHERE USER_ID= #{userId} 
	  ORDER BY POINT_NUM DESC LIMIT #{startRow}, #{pageSize}

mapper.insert:
	INSERT INTO POINT(
	 POINT_NUM
	,USER_ID
	,POINT_DATE
	,POINT_TYPE
	,POINT_NOW
	,POINT_CHARGE
	) 
values(
	 (SELECT A.POINTNUM
	    FROM (SELECT MAX(POINT_NUM) + 1 AS POINTNUM
	    	    FROM POINT) A)
	,#{userId}
	,#{pointDate}
	,#{pointType}
	,#{pointNow}
	,#{pointCharge}
	)

	INSERT INTO POINT(
	 POINT_NUM
	,USER_ID
	,POINT_DATE
	,POINT_TYPE
	,POINT_NOW
	,POINT_USED
	) 
values(
	 (SELECT A.POINTNUM
	    FROM (SELECT MAX(POINT_NUM) + 1 AS POINTNUM
	    	    FROM POINT) A)
	,#{userId}
	,#{pointDate}
	,#{pointType}
	,#{pointNow}
	,#{pointUsed}
	)
