mapper.select:
		SELECT COUNT(*)
		  FROM POINT
   		 WHERE USER_ID= #{userId}

mapper.insert:
	INSERT INTO POINT(
	 POINT_NUM
	,USER_ID
	,POINT_DATE
	,POINT_TYPE
	,POINT_NOW
	,POINT_CHARGE
	) 
values(
	 (SELECT A.POINTNUM
	    FROM (SELECT MAX(POINT_NUM) + 1 AS POINTNUM
	    	    FROM POINT) A)
	,#{userId}
	,#{pointDate}
	,#{pointType}
	,#{pointNow}
	,#{pointCharge}
	)

mapper.select:
		SELECT * 
		  FROM POINT
 		 WHERE USER_ID= #{userId}
 		   AND date_format(POINT_DATE, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
	  ORDER BY POINT_DATE DESC LIMIT #{startRow}, #{pageSize}
