mapper.update:
UPDATE USER_MYPAGE SET 
BOARD_COUNT = BOARD_COUNT + 1 
where USER_ID=#{userId}

UPDATE USER_MYPAGE SET 
REPLY_COUNT = REPLY_COUNT + 1 
where USER_ID=#{userId}

UPDATE USER_MYPAGE SET 
PRODUCT_LIKE = PRODUCT_LIKE + 1 
where USER_ID=#{userId}

UPDATE USER_MYPAGE SET 
BOARD_COUNT = BOARD_COUNT - 1 
where USER_ID=#{userId}

UPDATE USER_MYPAGE SET 
REPLY_COUNT = REPLY_COUNT - 1 
where USER_ID=#{userId}

UPDATE USER_MYPAGE SET 
PRODUCT_LIKE = PRODUCT_LIKE - 1 
where USER_ID=#{userId}
